const requireComponent = require.context(
    './components',
    true,
    /[\w-]+\.vue$/
);

export default {
    install() {
        requireComponent.keys().forEach((fileName) => {
            const componentConfig = requireComponent(fileName).default;
            Vue.component(componentConfig.name, componentConfig);
        });
    }
};

